function popup_open(item, video = '') {
  const activePopup = document.querySelectorAll('.popup._active');
  if (activePopup.length > 0) {
    popup_close('', false);
  }
  const curent_popup = document.querySelector(`.popup_${item}`);
  if (curent_popup && unlock) {
    if (video != '' && video != null) {
      const popup_video = document.querySelector('.popup_video');
      popup_video.querySelector('.popup__video').innerHTML = `<iframe src="https://www.youtube.com/embed/${video}?autoplay=1"  allow="autoplay; encrypted-media" allowfullscreen></iframe>`;
    }
    if (!document.querySelector('.menu__body._active, .in1-menu__body._active, .in4-menu__body._active, .in5-menu__body._active, .in7-menu__body._active')) {
      body_lock_add(500);
    }
    curent_popup.classList.add('_active');
    history.pushState('', '', `#${item}`);
  }
}
function popup_close(item, bodyUnlock = true) {
  if (unlock) {
    if (!item) {
      for (let index = 0; index < popups.length; index++) {
        const popup = popups[index];
        const video = popup.querySelector('.popup__video');
        if (video) {
          video.innerHTML = '';
        }
        popup.classList.remove('_active');
      }
    } else {
      const video = item.querySelector('.popup__video');
      if (video) {
        video.innerHTML = '';
      }
      item.classList.remove('_active');
    }
    if (!document.querySelector('.menu__body._active, .in1-menu__body._active , .in4-menu__body._active , .in5-menu__body._active, .in7-menu__body._active') && bodyUnlock) {
      body_lock_remove(500);
    }
    history.pushState('', '', window.location.href.split('#')[0]);
  }
}
