document.addEventListener('DOMContentLoaded', () => {
  const forms = document.querySelectorAll('form');
  if (forms.length > 0) {
    for (let index = 0; index < forms.length; index++) {
      const el = forms[index];
      el.addEventListener('submit', formSend);
    }
  }

  async function formSend(e) {
    const btn = e.target;
    const form = btn.closest('form');
    const error = form_validate(form);
    if (error === 0) {
      const formAction = form.getAttribute('action') ? form.getAttribute('action').trim() : '#';
      const formMethod = form.getAttribute('method') ? form.getAttribute('method').trim() : 'GET';

      // SendForm
      if (formAction.length > 2) {
        e.preventDefault();
        const formData = new FormData(form);
        form.classList.add('_sending');
        fetch(formAction, {
          method: formMethod,
          body: formData,
        })
          .then((response) => response.json())
          .then((response) => {
            console.log(response);
            document.querySelector('.popup_popup').classList.toggle('_active');
          })
          .catch((err) => {
            console.log(err);
            form.classList.remove('_sending');
          });
      }
      // If test
      if (form.hasAttribute('data-test')) {
        e.preventDefault();
        if (message) {
          popup_open(`${message}-message`);
        }
        form_clean(form);
      }
    } else {
      const form_error = form.querySelectorAll('._error');
      if (form_error && form.classList.contains('_goto-error')) {
        _goto(form_error[0], 1000, 50);
      }
      e.preventDefault();
    }
  }
  function form_validate(form) {
    let error = 0;
    const form_req = form.querySelectorAll('._req');
    if (form_req.length > 0) {
      for (let index = 0; index < form_req.length; index++) {
        const el = form_req[index];
        if (!_is_hidden(el)) {
          error += form_validate_input(el);
        }
      }
    }
    return error;
  }
  function form_validate_input(input) {
    let error = 0;
    const input_g_value = input.getAttribute('data-value');

    if (input.getAttribute('name') == 'email' || input.classList.contains('_email')) {
      if (input.value != input_g_value) {
        const em = input.value.replace(' ', '');
        input.value = em;
      }
      if (email_test(input) || input.value == input_g_value) {
        form_add_error(input);
        error++;
      } else {
        form_remove_error(input);
      }
    } else if (input.getAttribute('type') == 'checkbox' && input.checked == false) {
      form_add_error(input);
      error++;
    } else if (input.value == '' || input.value == input_g_value) {
      form_add_error(input);
      error++;
    } else {
      form_remove_error(input);
    }
    return error;
  }
  function form_add_error(input) {
    input.classList.add('_error');
    input.parentElement.classList.add('_error');

    const input_error = input.parentElement.querySelector('.form__error');
    if (input_error) {
      input.parentElement.removeChild(input_error);
    }
    const input_error_text = input.getAttribute('data-error');
    if (input_error_text && input_error_text != '') {
      input.parentElement.insertAdjacentHTML('beforeend', `<div class="form__error">${input_error_text}</div>`);
    }
  }
  function form_remove_error(input) {
    input.classList.remove('_error');
    input.parentElement.classList.remove('_error');

    const input_error = input.parentElement.querySelector('.form__error');
    if (input_error) {
      input.parentElement.removeChild(input_error);
    }
  }
  function form_clean(form) {
    const inputs = form.querySelectorAll('input,textarea');
    for (let index = 0; index < inputs.length; index++) {
      const el = inputs[index];
      el.parentElement.classList.remove('_focus');
      el.classList.remove('_focus');
      el.value = el.getAttribute('data-value');
    }
    const checkboxes = form.querySelectorAll('.checkbox__input');
    if (checkboxes.length > 0) {
      for (let index = 0; index < checkboxes.length; index++) {
        const checkbox = checkboxes[index];
        checkbox.checked = false;
      }
    }
    const selects = form.querySelectorAll('select');
    if (selects.length > 0) {
      for (let index = 0; index < selects.length; index++) {
        const select = selects[index];
        const select_default_value = select.getAttribute('data-default');
        select.value = select_default_value;
        select_item(select);
      }
    }
  }
});

